import * as React from 'react';
import CollapsedButton from '@/pages/home/components/collapsed_button';
import styles from '@/pages/home/index.module.css';
import { Dispatch } from "redux"
import { useSelector, useDispatch } from "react-redux"
import { RootStore } from "@/store";
import * as types from './store/types'
import {
  TwitterOutlined,
  DownOutlined
} from '@ant-design/icons';
import { Avatar, Dropdown, Menu } from 'antd';
import * as actions from './store/actions';
import { useHistory } from 'react-router-dom'

const Header: React.FC = () => {
  let history = useHistory()
  const dispatch: Dispatch<any> = useDispatch()
  const headlerState = useSelector((state: RootStore) => state.header);
  const signout = () => dispatch(actions.signoutRequest({
    history: history,
    data: {}
  } as types.GetAccountRequestWithProps))
  React.useEffect(() => {
    dispatch(actions.getAccountRequest({
      history: history,
      data: {}
    } as types.GetAccountRequestWithProps))
  }, [])

  const menu = (
    <Menu>
      <Menu.Item>
        <span>
          设置
        </span>
      </Menu.Item>
      <Menu.Item>
        <span onClick={signout}>
          退出
        </span>
      </Menu.Item>

    </Menu>
  );
  return <div className={styles.headerContainer}>
    <div className={styles.logoContainer}>
      <TwitterOutlined className={styles.logo} />
      <strong className={styles.title}>SHAKEBOOK</strong>
      <CollapsedButton />
    </div>
    <div className={styles.account}>
      <Avatar size={24} src={process.env.PUBLIC_URL + '/logo512.png'} />&nbsp;
      <Dropdown overlay={menu} trigger={['click']}>
        <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
          <strong>{headlerState.account.accountEmail}</strong> <DownOutlined />
        </a>
      </Dropdown>
    </div>

  </div>
}
export default Header
