import { manager } from '@/service/api/v1/manager_pb'
import * as types from './types'
import axios from 'axios';
import camelcaseKeys from 'camelcase-keys'
import api from '@/requestAPI';
import { notification } from 'antd';
import { Dispatch } from "redux"

const getRoleListAction = (res: manager.IRole[]): types.GetRoleListAction => {
  return {
    type: types.GET_ROLE_LIST,
    data: res
  }
}

export function createRoleRequest(req: types.RoleRequestWithProps) {
  return (dispatch: Dispatch<any>) => {
    axios({
      method: 'post',
      url: api.role,
      data: req.data,
      headers: {
        "Grpc-metadata-token": "BEARER " + localStorage.getItem("token")
      }
    }).then(function (res) {
      let ret: manager.IResponse =
        manager.Response.fromObject(
          camelcaseKeys(res.data as Object, {
            deep: true
          }),
        )

      if (res.status === 200 && ret.success) {
        dispatch(getRoleListRequest({
          history: req.history
        } as types.GetRoleListRequestWithProps
        ))
        notification['success']({
          message: '信息提示',
          description: "新增成功"
        });
      } else {
        notification['warning']({
          message: '信息提示',
          description: ret.message
        });
        localStorage.removeItem("token")
        req.history.push('/login')

      }
    })
      .catch(function (error) {
        notification['error']({
          message: '信息提示',
          description: "请稍后重试"
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      });
  }
}

