          }),
        )

      if (res.status === 200 && ret.success) {
        dispatch(getRoleListRequest({
          history: req.history
        } as types.GetRoleListRequestWithProps
        ))
        notification['success']({
          message: '信息提示',
          description: "更新成功"
        });
      } else {
        notification['warning']({
          message: '信息提示',
          description: ret.message
        });
        localStorage.removeItem("token")
        req.history.push('/login')

      }
    })
      .catch(function (error) {
        notification['error']({
          message: '信息提示',
          description: "请稍后重试"
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      });
  }
}

//查询角色列表
export function getRoleListRequest(req: types.GetRoleListRequestWithProps) {
  return (dispatch: types.GetRoleDispatch) => {
    axios({
      method: 'get',
      url: api.role,
      headers: {
        "Grpc-metadata-token": "BEARER " + localStorage.getItem("token")
      }
    }).then(function (res) {
      let ret: manager.IGetRoleListResponse =
        manager.GetRoleListResponse.fromObject(
          camelcaseKeys(res.data as Object, {
            deep: true
          }),
        )

      if (res.status === 200 && ret.success) {
        dispatch(getRoleListAction(ret.data as manager.IRole[]))

      } else {
        notification['warning']({
          message: '信息提示',
          description: ret.message
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      }
    })
      .catch(function () {
        notification['error']({
          message: '信息提示',
          description: "请稍后重试"
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      });
  }
}

//删除角色
