  return (dispatch: Dispatch<any>) => {
    axios({
      method: 'delete',
      url: api.role,
      data: req.data,
      headers: {
        "Grpc-metadata-token": "BEARER " + localStorage.getItem("token")
      }
    }).then(function (res) {
      let ret: manager.IResponse =
        manager.Response.fromObject(
          camelcaseKeys(res.data as Object, {
            deep: true
          }),
        )

      if (res.status === 200 && ret.success) {
        dispatch(getRoleListRequest({
          history: req.history
        } as types.GetRoleListRequestWithProps
        ))

      } else {
        notification['warning']({
          message: '信息提示',
          description: ret.message
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      }
    })
      .catch(function () {
        notification['error']({
          message: '信息提示',
          description: "请稍后重试"
        });
        localStorage.removeItem("token")
        req.history.push('/login')
      });
  }
}
