import * as types from "./types"
const initialState: types.RoleState = {
  roleList: [],
  checkedKeys: []
}

function reducer(
  state: types.RoleState = initialState,
  action: any
): types.RoleState {
  switch (action.type) {
    case types.GET_ROLE_LIST:
      const a = action as types.GetRoleListAction
      return {
        ...state,
        roleList: [...a.data!],
      }
    default:
      return state
  }
}


export default reducer;
