import React from 'react';
import { Table, Modal, Space, Button, Input } from 'antd';
import styles from './index.module.css';
import * as types from './store/types';
import { manager } from '@/service/api/v1/manager_pb'
const data: manager.IRole[] = [
  {
    id: 1,
    roleName: "超级管理员"
  },
  {
    id: 2,
    roleName: "超级管理员"
  },
  {
    id: 3,
    roleName: "超级管理员"
  },
];

const Role: React.FC = () => {
  const [visible, setVisible] = React.useState(false)
  const [name, setName] = React.useState("")
  const submit = () => {

  }
  const showHandler = (text: string) => {
    setName(text);
    setVisible(true)
  }
  const columns = [
    {
      title: '角色名称',
      dataIndex: 'name',
      key: 'name',
      render: (text: string) => text,
    },
    {
      title: '操作',
      key: 'action',
      render: (text: string, record: types.Role) => (

        <Space size="small">
          <Button type="primary" ghost size="small">关联菜单</Button>
          <Button type="primary" ghost size="small" onClick={() => showHandler(record.name)}>修改</Button>
          <Button type="primary" ghost danger size="small">删除</Button>
        </Space>
      ),
    },
  ];
  return (
    <div>
      <div className={styles.form}>
        <Button type="primary" onClick={() => showHandler("")}>新增</Button>
        <Modal
          title="角色名称"
          visible={visible}
          onOk={submit}
          onCancel={() => setVisible(false)}
          okText="确认"
          cancelText="取消"
        >
          <Input value={name} onChange={(e) => setName(e.target.value)} />
        </Modal>
      </div>
      <Table columns={columns} dataSource={data} pagination={false} />
    </div>
  )
}
export default Role
