import * as React from 'react';
import { Table, Button, Modal, Input, Form, TreeSelect, message } from 'antd';
import { Dispatch } from "redux"
import { useSelector, useDispatch } from "react-redux"
import { manager } from '@/service/api/v1/manager_pb'
import * as actions from './store/actions'
import { RootStore } from "@/store";

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 15 },
};
const columns = [
  {
    title: '菜单名称',
    dataIndex: 'title',
    key: 'title',
  },
  {
    title: '菜单路由',
    dataIndex: 'menuRouter',
    key: 'menuRouter',
  },
  {
    title: '菜单图标',
    dataIndex: 'menuIcon',
    key: 'menuIcon',
  },
];

const data = [
  {
    key: 1,
    name: 'John Brown sr.',
    age: 60,
    address: 'New York No. 1 Lake Park',
    children: [
      {
        key: 11,
        name: 'John Brown',
        age: 42,
        address: 'New York No. 2 Lake Park',
      },
      {
        key: 12,
        name: 'John Brown jr.',
        age: 30,
        address: 'New York No. 3 Lake Park',
        children: [
          {
            key: 121,
            name: 'Jimmy Brown',
            age: 16,
            address: 'New York No. 3 Lake Park',
          },
        ],
      },
      {
        key: 13,
        name: 'Jim Green sr.',
        age: 72,
        address: 'London No. 1 Lake Park',
        children: [
          {
            key: 131,
            name: 'Jim Green',
            age: 42,
            address: 'London No. 2 Lake Park',
            children: [
              {
                key: 1311,
                name: 'Jim Green jr.',
                age: 25,
                address: 'London No. 3 Lake Park',
              },
              {
                key: 1312,
                name: 'Jimmy Green sr.',
                age: 18,
                address: 'London No. 4 Lake Park',
              },
            ],
          },
        ],
      },
    ],
  },
  {
    key: 2,
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
  },
];



const Menu: React.FC = () => {
  const [visible, setVisible] = React.useState(false)

  const [menuName, setMenuName] = React.useState("")
  const [menuIcon, setMenuIcon] = React.useState("")
  const [menuRouter, setMenuRouter] = React.useState("")
  const [parentId, setParentId] = React.useState<number | string>("")
  const [id, setId] = React.useState<null | number>(null)
  const dispatch: Dispatch<any> = useDispatch()
  const menuState = useSelector((state: RootStore) => state.menu);


  React.useEffect(() => {
    dispatch(actions.getMenuListRequest())
  }, [dispatch])

  const submit = () => {
    if (!menuName) {
      message.error('菜单名称不能为空');
      return
    }
    if (id) {
      // dispatch(actions.UpdateRoleRequest({
      //   {
      //     roleName: menuName,
      //     id: id,
      //   } as manager.ICreateMenuRequest)
    } else {
      dispatch(actions.createMenuRequest({
        menuName: menuName.trim(),
        menuIcon: menuIcon.trim(),
        menuRouter: menuRouter.trim(),
        parentId: Number(parentId),
      } as manager.ICreateMenuRequest))
    }
    cancelModel()
  }

  const showModel = (record: manager.IMenuInfo) => {
    setMenuName(record.menuName!);
    setId(record.id!);
    setVisible(true)
  }

  const cancelModel = () => {
    setVisible(false)
    setMenuName("");
    setId(null)
  }

  return <>
    <div style={{ marginBottom: '10px' }}>
      <Button type="primary" onClick={() => showModel({} as manager.ICreateMenuRequest)}>新增</Button>
      <Modal
        title="菜单"
        visible={visible}
        onOk={submit}
        onCancel={cancelModel}
        okText="确认"
        cancelText="取消"
        style={{ top: 20 }}
      >
        <Form {...layout}>
          <Form.Item label="上级菜单">
            <TreeSelect
              allowClear={true}
              style={{ width: '100%' }}
              value={parentId}
              dropdownStyle={{ maxHeight: 600, overflow: 'auto' }}
              treeData={menuState.menuList}
              placeholder="请选择..."
              treeDefaultExpandAll
              onChange={(v) => setParentId(v)}
            />
          </Form.Item >
          <Form.Item label={<span><strong style={{ color: "red" }}>*</strong>菜单名称</span>}>
            <Input
              onChange={(e) => setMenuName(e.target.value)}
              value={menuName}
            />
          </Form.Item >
          <Form.Item label="菜单路由">
            <Input
              onChange={(e) => setMenuRouter(e.target.value)}
              value={menuRouter}
            />
          </Form.Item >
          <Form.Item label="菜单图标">
            <Input
              onChange={(e) => setMenuIcon(e.target.value)}
              value={menuIcon}
            />
          </Form.Item >
        </Form>
      </Modal>
    </div>
    <Table
      columns={columns}
      dataSource={menuState.menuList}
      pagination={false}
    />
  </>

}

export default Menu
