import React from 'react';
import { Table, Tag, Space, Button } from 'antd';
import styles from './index.module.css';
import types from './store/types';
const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: (text: string) => <a>{text}</a>,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: (tags: any) => (
      <>
        {tags.map((tag: any) => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text: string, record: any) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

const data: types.Role[] = [
  {
    id: 1,
    name: 'John Brown',

  },
  {
    id: 2,
    name: 'Jim Green',

  },
  {
    id: 3,
    name: 'Joe Black',

  },
];
const Role: React.FC = () => {
  const [text] = React.useState("yangjiafeng")
  return (
    <div>
      <div className={styles.form}>
        <Button type="primary">新增</Button>
      </div>
      <Table columns={columns} dataSource={data} />
    </div>
  )
}
export default Role
