import * as React from 'react';
import { Table, Button, Modal, Input } from 'antd';
import styles from './index.module.css';
import { Dispatch } from "redux"
import { useSelector, useDispatch } from "react-redux"
import { manager } from '@/service/api/v1/manager_pb'
const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
    width: '12%',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    width: '30%',
    key: 'address',
  },
];

const data = [
  {
    key: 1,
    name: 'John Brown sr.',
    age: 60,
    address: 'New York No. 1 Lake Park',
    children: [
      {
        key: 11,
        name: 'John Brown',
        age: 42,
        address: 'New York No. 2 Lake Park',
      },
      {
        key: 12,
        name: 'John Brown jr.',
        age: 30,
        address: 'New York No. 3 Lake Park',
        children: [
          {
            key: 121,
            name: 'Jimmy Brown',
            age: 16,
            address: 'New York No. 3 Lake Park',
          },
        ],
      },
      {
        key: 13,
        name: 'Jim Green sr.',
        age: 72,
        address: 'London No. 1 Lake Park',
        children: [
          {
            key: 131,
            name: 'Jim Green',
            age: 42,
            address: 'London No. 2 Lake Park',
            children: [
              {
                key: 1311,
                name: 'Jim Green jr.',
                age: 25,
                address: 'London No. 3 Lake Park',
              },
              {
                key: 1312,
                name: 'Jimmy Green sr.',
                age: 18,
                address: 'London No. 4 Lake Park',
              },
            ],
          },
        ],
      },
    ],
  },
  {
    key: 2,
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
  },
];


const Menu: React.FC = () => {
  const [visible, setVisible] = React.useState(false)

  const [menuName, setMenuName] = React.useState("")
  const [menuIcon, setMenuIcon] = React.useState("")
  const [menuRouter, setMenuRouter] = React.useState("")
  const [parentId, setParentId] = React.useState<null | number>(null)
  const [id, setId] = React.useState<null | number>(null)
  const dispatch: Dispatch<any> = useDispatch()
  const submit = () => {
    if (id) {
      dispatch(actions.UpdateRoleRequest({
        history: history,
        data: {
          roleName: menuName,
          id: id,
        } as manager.IRole
      } as types.RoleRequestWithProps))
    } else {
      dispatch(actions.createRoleRequest({
        history: history,
        data: {
          menuName: menuName,
          menuIcon: menuIcon,
          menuRouter: menuRouter,
          parentId: parentId,
        } as manager.ICreateMenuRequest
      } as types.RoleRequestWithProps))
    }
    cancelModel()
  }

  const showModel = (record: manager.IMenuInfo) => {
    setMenuName(record.menuName!);
    setId(record.id!);
    setVisible(true)
  }

  const cancelModel = () => {
    setVisible(false)
    setMenuName("");
    setId(null)
  }

  return <>
    <div className={styles.form}>
      <Button type="primary" onClick={() => showModel({ id: null, roleName: "" })}>新增</Button>
      <Modal
        title="菜单名称"
        visible={visible}
        onOk={submit}
        onCancel={cancelModel}
        okText="确认"
        cancelText="取消"
      >
        <Input value={menuName} onChange={(e) => setMenuName(e.target.value)} />
      </Modal>
    </div>
    <Table
      columns={columns}
      dataSource={data}
      pagination={false}
    />
  </>

}

export default Menu
