import React, { useEffect } from 'react';
import { Table, Modal, Space, Button, Input, message, Tag } from 'antd';
import { Dispatch } from "redux"
import { useSelector, useDispatch } from "react-redux"
import * as actions from './store/actions'
import { RootStore } from "@/store";
const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: (text: string) => <a>{text}</a>,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: (tags: any) => (
      <>
        {tags.map((tag: string) => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text: string, record: any) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

const data = [
  {
    key: '1',
    name: 'John Brown',
    age: 32,
    address: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
  },
  {
    key: '2',
    name: 'Jim Green',
    age: 42,
    address: 'London No. 1 Lake Park',
    tags: ['loser'],
  },
  {
    key: '3',
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
    tags: ['cool', 'teacher'],
  },
];
const Account: React.FC = () => {
  const dispatch: Dispatch<any> = useDispatch()
  const accountState = useSelector((state: RootStore) => state.account);
  React.useEffect(() => {
    dispatch(actions.getAccountListRequest())
  }, [dispatch])
  return (<>
    <div style={{ marginBottom: "20px" }}>
      sdfsd
        </div>
    <Table columns={columns} dataSource={accountState.accountList} />
  </>
  )
}
export default Account
