import axios from 'axios';
import { shakebook } from '@/service/api/v1/account_pb'
import camelcaseKeys from 'camelcase-keys'
import { notification } from 'antd';
export const SignIn = (req: shakebook.ISignInRequest) => {
  axios({
    method: 'post',
    url: '/api/v1/signin',
    data: req,
  }).then(function (res) {
    if (res.status === 200) {
      let ret: shakebook.ISignInResponse =
        shakebook.SignInResponse.fromObject(
          camelcaseKeys(res.data as Object)
        )
      console.log(ret);
      localStorage.setItem("token", ret.token as string);
    }


  })
    .catch(function (error) {
      notification['error']({
        message: 'Notification Title',
        description:
          'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
      });
    });
}
