import * as React from 'react';
import { Menu } from 'antd';
import {
  AppstoreOutlined,
  PieChartOutlined,
  DesktopOutlined,
  ContainerOutlined,
  MailOutlined,

} from '@ant-design/icons';
import IconFont from '@/common/icon';
import { RootStore } from '@/store';
import { useSelector } from 'react-redux';
import { Link } from "react-router-dom";
import * as menuTypes from '@/pages/content/pages/menu/store/types'

const { SubMenu } = Menu;

const SiderBarMenus: React.FC = () => {
  const homeState = useSelector((state: RootStore) => state.home);
  const headerState = useSelector((state: RootStore) => state.header);

  React.useEffect(() => {
    mapMenuList(headerState.accountMenus)
  }, [])
  //递归菜单
  const mapMenuList = (menus: menuTypes.MenuTree[]) => {
    return (
      menus.map(i => {
        if (i.children && i.children.length > 0) {
          return (
            <SubMenu
              key={i.value}
              title={<span>
                <IconFont style={{ color: "#048804", fontSize: "20px", fontWeight: "bold" }} type={i.menuIcon} />
                <span>{i.title}</span>
              </span>
              }
            >
              {mapMenuList(i.children)}
            </SubMenu>
          )
        } else {
          return <Menu.Item key={i.value}>
            <Link to={i.menuRouter}>{i.title}</Link>
          </Menu.Item>
        }
      })
    )
  }
  return (
    <div style={{ width: homeState.collapsed ? 80 : 256 }}>
      <Menu
        defaultSelectedKeys={['1']}
        defaultOpenKeys={['sub1']}
        mode="inline"
        theme="dark"
        inlineCollapsed={homeState.collapsed}
      >
        <Menu.Item key="1" icon={<PieChartOutlined />}>
          <Link to="/role">角色管理</Link>
        </Menu.Item>
        <Menu.Item key="2" icon={<DesktopOutlined />}>
          <Link to="/menu">菜单管理</Link>
        </Menu.Item>
        <Menu.Item key="31" icon={<DesktopOutlined />}>
          <Link to="/account">用户管理</Link>
        </Menu.Item>
        <Menu.Item key="3" icon={<ContainerOutlined />}>
          Option 3
          </Menu.Item>
        <SubMenu key="sub1" icon={<MailOutlined />} title="Navigation One">
          <Menu.Item key="5">Option 5</Menu.Item>
          <Menu.Item key="6">Option 6</Menu.Item>
          <Menu.Item key="7">Option 7</Menu.Item>
          <Menu.Item key="8">Option 8</Menu.Item>
        </SubMenu>
        <SubMenu key="sub2" icon={<AppstoreOutlined />} title="Navigation Two">
          <Menu.Item key="9">Option 9</Menu.Item>
          <Menu.Item key="10">Option 10</Menu.Item>
          <SubMenu key="sub3" title="Submenu">
            <Menu.Item key="11">Option 11</Menu.Item>
            <Menu.Item key="12">Option 12</Menu.Item>
          </SubMenu>
        </SubMenu>
      </Menu>
    </div>
  );
}


export default SiderBarMenus
